package bitcamp.lms;

public class App {
  public static void main(String[] args) {

    java.util.Scanner key = new java.util.Scanner(System.in);

    final int LENGTH = 2;
    int[] num = new int[LENGTH];
    String[] className = new String[LENGTH];
    String[] classContent = new String[LENGTH];
    String[] stDate = new String[LENGTH];
    String[] edDate = new String[LENGTH];
    int[] tot = new int[LENGTH];
    int[] day = new int[LENGTH];
    int i = 0;

    ok:{
      while(i<LENGTH) {
        if (i >= LENGTH) {
          System.out.println("꽉참");
        }
        String ans = null;
        System.out.print("번호 : ");
        num[i] = Integer.parseInt(key.nextLine());
        System.out.print("수업명 : ");
        className[i] = key.nextLine();
        System.out.print("수업내용 : ");
        classContent[i] = key.nextLine();
        System.out.print("시작일 : ");
        stDate[i] = key.nextLine();
        System.out.print("종료일 : ");
        edDate[i] = key.nextLine();
        System.out.print("총 수업시간 : ");
        tot[i] = Integer.parseInt(key.nextLine());
        System.out.print("일 수업시간 : ");
        day[i] = Integer.parseInt(key.nextLine());

        System.out.print("계속 입력하시겠습니까 y/n");
        ans = key.nextLine();

        if (ans.equalsIgnoreCase("y") || ans.equals("")) {
          i++;
          continue;
        } else {
          int j = 0;
          while (j <= i) {
            System.out.println(num[j]+" "+ className[j]+" "+ stDate[j] + " ~ "
                + edDate[j]+" "+tot[j]);
            j++;
          }
          key.close();
          break ok;
        }
      }
    }
















  }
}
